insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А после того как Иисус ародился в 1бВифлееме Иудейском в дни царя вИрода, вот, прибыли в Иерусалим гволхвы с двостока',2,1,2,1);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И сказали: Где Тот, кто родился 1аЦарём Иудейским? Ибо мы увидели Его 2бзвезду при её восхождении и пришли впоклониться Ему.',2,1,2,2);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И, услышав это, царь Ирод встревожился, и весь Иерусалим с ним.',2,1,2,3);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И, собрав всех главных 1асвященников и бкнижников народа, он спросил у них, где родится Христос.',2,1,2,4);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И они сказали ему: В аВифлееме Иудейском, ибо так написано через пророка:',2,1,2,5);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('«аИ ты, Вифлеем, земля Иуды, отнюдь не малейший среди 1предводителей Иуды; ибо из тебя выйдет бПравитель, который будет впасти Мой народ, Израиля».',2,1,2,6);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Тогда Ирод, тайно позвав волхвов, точно узнал у них время появления звезды.',2,1,2,7);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И, послав их в Вифлеем, сказал: Пойдите, точно выясните о ребёнке. А когда найдёте Его, сообщите мне, чтобы и я пришёл и поклонился Ему.',2,1,2,8);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И они, выслушав царя, пошли, и вот, 1азвезда, которую они увидели при её восхождении, вела их, пока не пришла и не остановилась над местом, где был ребёнок.',2,1,2,9);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И, увидев звезду, они чрезвычайно обрадовались великой радостью.',2,1,2,10);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И, войдя в 1дом, они увидели аребёнка с бМарией, Его матерью. И, пав, 2поклонились Ему и, открыв свои сокровищницы, преподнесли Ему 3вдары: гзолото, дладан и емирру.',2,1,2,11);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И, получив во асне божественное указание не 1возвращаться к Ироду, они 2другим путём удалились в свою страну.',2,1,2,12);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А когда они удалились, вот, аангел Господень является во бсне Иосифу и говорит: Встань, возьми ребёнка и Его мать, и беги в вЕгипет, и будь там, пока я не скажу тебе; ибо Ирод намерен искать ребёнка, чтобы уничтожить Его.',2,1,2,13);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И он встал и взял ребёнка и Его мать ночью, и удалился в Египет,',2,1,2,14);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И был там до смерти Ирода, чтобы 1аисполнилось сказанное Господом через пророка, который говорит: «бИз Египта Я призвал Моего 2вСына».',2,1,2,15);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Тогда Ирод, увидев, что он осмеян волхвами, сильно разгневался и, послав людей, 1расправился со всеми мальчиками, которые были в Вифлееме и во всех его пределах, от двух лет и младше, согласно времени, которое точно узнал у волхвов.',2,1,2,16);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Тогда аисполнилось сказанное через пророка Иеремию, который говорит:',2,1,2,17);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('«аГолос в Раме был слышен, плач и великое сетование: 1Рахиль оплакивала своих детей и не хотела утешиться, потому что их уже нет».',2,1,2,18);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А когда Ирод умер, вот, ангел Господень является во асне Иосифу в Египте',2,1,2,19);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И говорит: Встань, возьми ребёнка и Его мать и иди в землю Израиля, ибо аумерли те, кто ищет 1жизни ребёнка.',2,1,2,20);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И он встал и взял ребёнка и Его мать и вошёл в землю Израиля.',2,1,2,21);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Но, услышав, что Архелай царствует над Иудеей вместо своего отца Ирода, он побоялся идти туда и, получив во асне божественное указание, удалился в области 1бГалилеи.',2,1,2,22);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И он пришёл и поселился в городе, который называется 1аНазарет, чтобы бисполнилось сказанное через 2пророков, что Он будет назван 3Назарянином.',2,1,2,23);
