insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('<1>Но остерегайтесь творить свою <2>праведность <3>перед людьми с тем, чтобы они <а>смотрели на вас; а иначе нет вам награды у вашего <4>Отца, который на небесах.',2,1,6,1);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Итак, когда <а>подаёшь милостыню, не <1>труби перед собой, как делают <2б>лицемеры в <3>синагогах и на улицах, чтобы <в>прославляли их люди. Истинно говорю вам: они сполна получили свою награду.',2,1,6,2);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А ты, когда подаёшь милостыню, — пусть твоя <1>левая рука не знает, что делает правая,',2,1,6,3);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Чтобы твоя милостыня была <1а>тайне, <3б>воздаст тебе.',2,1,6,4);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И когда молитесь, не будьте как <а>лицемеры, потому что они любят <б>молиться, стоя в синагогах и на углах улиц, чтобы показать себя людям. Истинно говорю вам: они сполна получили <1>свою награду.',2,1,6,5);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А ты, когда молишься, войди в свою <1>внутреннюю комнату и, <а>заперев дверь, помолись твоему Отцу, который <в>тайне; и твой Отец, который видит <б>втайне, воздаст тебе.',2,1,6,6);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А молясь, <1>не говорите пустых слов, как язычники; ибо они думают, что в своём <а>многословии будут услышаны.',2,1,6,7);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Итак, не уподобляйтесь им, ибо ваш Отец <а>знает, в чём вы имеете нужду, прежде чем вы <1>попросите Его.',2,1,6,8);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('<а>Вы же молитесь <1>так: <б>Отец наш, который на небесах, <2>пусть <в>освятится Твоё имя;',2,1,6,9);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Пусть придёт Твоё <а>царство; пусть исполнится Твоя <б>воля и на <1>земле, как на небе.',2,1,6,10);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('<1>Дай нам <2>сегодня наш <3>насущный хлеб.',2,1,6,11);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И <а>прости нам наши долги, как и мы <1>простили нашим должникам.',2,1,6,12);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И <1>не введи нас в <а>искушение, а <б>избавь нас от <2в>лукавого. <3>Ибо Твоё есть <4г>царство, и <д>сила, и <е>слава вовеки. <5ж>Аминь.',2,1,6,13);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('<1>Ибо если вы <а>простите людям их проступки, то простит и вам ваш небесный Отец;',2,1,6,14);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А если <а>не простите людям их проступков, то и ваш Отец не простит ваших проступков.',2,1,6,15);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И когда <1а>поститесь, не будьте как угрюмые лицемеры, ибо они искажают свои лица, чтобы <б>показать себя людям постящимися. Истинно говорю вам: они сполна получили свою награду.',2,1,6,16);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А ты, когда постишься, <а>помажь свою голову и умой своё лицо,',2,1,6,17);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Чтобы показать себя постящимся не людям, а твоему Отцу, который <а>втайне; и твой Отец, который видит втайне, воздаст тебе.',2,1,6,18);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('<1а>Не копите себе сокровищ на земле, где <б>моль и ржавчина уничтожают и где воры подкапывают и крадут,',2,1,6,19);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А <1>копите себе <а>сокровища на небе, где ни моль, ни ржавчина не уничтожают и где воры не <б>подкапывают и не крадут.',2,1,6,20);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Ибо где твоё сокровище, там будет и твоё <1а>сердце.',2,1,6,21);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('<а>Светильник тела — глаз. Итак, если твой глаз будет <1>прост, то всё твоё тело будет светло;',2,1,6,22);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А если твой глаз будет <1>зол, то всё твоё тело будет темно. Если же <2>свет, который в тебе, есть <а>тьма, то как велика тьма!',2,1,6,23);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Никто не может <1>служить <а>двум господам, ибо или одного будет ненавидеть, а другого — любить, или <2>одного будет держаться, а другого — презирать. Не можете Богу служить и <3б>мамоне.',2,1,6,24);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Поэтому говорю вам: <а>не <б>беспокойтесь ни о вашей <1>жизни — что вам есть или что вам пить, ни о вашем теле — во что вам одеться. Разве жизнь не <2>больше пищи, а тело — одежды?',2,1,6,25);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Взгляните на <а>небесных птиц. Они не сеют, и не жнут, и не собирают в амбары, однако ваш небесный Отец кормит их. Разве вы не дороже их?',2,1,6,26);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И кто из вас может, беспокоясь, прибавить к своему <1>росту хотя бы один локоть?',2,1,6,27);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И об <а>одежде зачем <б>беспокоитесь? Присмотритесь к полевым <1в>лилиям, как они растут. Они не трудятся и не прядут.',2,1,6,28);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Но говорю вам, что даже <а>Соломон во всей своей славе не одевался так, как одна из них.',2,1,6,29);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Если же полевую <а>траву, которая сегодня есть, а завтра будет брошена в печь, Бог так наряжает, то тем более не <б>оденет ли вас, <в>маловеры?',2,1,6,30);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Итак, не <а>беспокойтесь и не говорите: «Что нам есть?», или «Что нам пить?», или «Во что нам одеться?»',2,1,6,31);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Ибо всего этого с беспокойством ищут язычники. Ибо ваш <1>небесный Отец знает, что вы <а>нуждаетесь во всём этом.',2,1,6,32);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А ищите прежде Его <1а>царства и Его <б>праведности, и всё это будет прибавлено вам.',2,1,6,33);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Итак, не <а>беспокойтесь о <1б>завтрашнем дне, ибо завтрашний день будет сам беспокоиться о себе. Достаточно для каждого дня своего <2>зла.',2,1,6,34);
