insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('<1а>В тот день Иисус, выйдя из <б>дома, сел у моря.',2,1,13,1);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И собрались к Нему большие толпы, так что Он вошёл в <1а>лодку и сел, а вся толпа стояла на берегу.',2,1,13,2);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И Он сказал им многое в притчах и говорил: <1>Вот, вышел <2а>сеятель сеять.',2,1,13,3);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И когда он сеял, одни <1а>семена упали <2б>у дороги, и прилетели <3б>птицы и склевали их.',2,1,13,4);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А другие упали на <1а>каменистые места, где земли у них было немного, и сразу же взошли, из-за того что земля у них была <б>неглубока.',2,1,13,5);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А когда взошло <1а>солнце, их опалило; и они, из-за того что не имели корня, засохли.',2,1,13,6);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А другие упали на <1а>терновник, и терновник поднялся и заглушил их.',2,1,13,7);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Но другие упали на <1а>хорошую землю и давали плод: одно — <б>стократный, одно — шестидесятикратный, а одно — тридцатикратный.',2,1,13,8);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Кто имеет <1а>уши, чтобы слышать, пусть слышит.',2,1,13,9);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('<а>И ученики, подойдя, сказали Ему: Почему Ты говоришь им в притчах?',2,1,13,10);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И Он сказал им в ответ: Потому что вам дано узнать <1а>тайны <б>царства небес, а им — не дано.',2,1,13,11);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Ибо <1а>кто имеет, тому будет дано и тот будет изобиловать; а <2>кто не имеет, у того будет взято и то, что он имеет.',2,1,13,12);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Поэтому говорю им в притчах, потому что они смотрят и <а>не видят, слушают и не слышат и не понимают.',2,1,13,13);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И исполняется на них пророчество Исайи, которое говорит: «<а>Слухом будете слышать и ни в коем случае не поймёте, глядя будете смотреть и ни в коем случае не увидите.',2,1,13,14);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Ибо ожирело сердце этого народа, и ушами они с трудом слышали, и свои <а>глаза они закрыли, чтобы не увидеть глазами, и не услышать ушами, и не понять сердцем, и не обратиться, и Я исцелю их».',2,1,13,15);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Но <а>блаженны ваши глаза, потому что видят, и ваши уши — потому что слышат.',2,1,13,16);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Ибо истинно говорю вам, что многие пророки и праведники желали <а>увидеть то, что вы <1>видите, и не увидели, и услышать то, что вы слышите, и не услышали.',2,1,13,17);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('<а>Вы же выслушайте притчу о сеявшем.',2,1,13,18);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Когда кто-нибудь слышит слово царства и не понимает, приходит <1а>лукавый и выхватывает то, что посеяно в его сердце. Это — посеянный у дороги.',2,1,13,19);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А посеянный на каменистые места — это тот, кто слышит слово и сразу же с радостью принимает его,',2,1,13,20);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Но он не имеет в себе корня — он держится недолго, а когда наступает скорбь или гонение из-за слова, сразу же <а>претыкается.',2,1,13,21);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А посеянный в терновник — это тот, кто слышит слово, а <а>беспокойство этого <б>века и обманчивость <в>богатства совсем заглушают слово, и оно становится бесплодным.',2,1,13,22);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А посеянный на хорошую землю — это тот, кто слышит слово и понимает, кто как раз и <а>плодоносит и приносит: один — стократный плод, один — шестидесятикратный, а один — тридцатикратный.',2,1,13,23);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Другую притчу предложил Он им и сказал: <1а>Царство небес стало подобно <б>человеку, который посеял в своём <в>поле хорошее <в>семя.',2,1,13,24);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А когда <1>люди спали, пришёл его <а>враг, посеял <2б>плевелы среди <3>пшеницы и ушёл.',2,1,13,25);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('А когда проросла зелень и принесла плод, тогда стали видны и плевелы.',2,1,13,26);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И рабы хозяина дома пришли и сказали ему: Господин, разве не хорошее семя ты посеял в своём поле? Откуда же взялись плевелы?',2,1,13,27);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И он сказал им: Это сделал враг. — А рабы говорят ему: Тогда хочешь, мы пойдём <1>соберём их?',2,1,13,28);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Но он говорит: Нет, чтобы, <1>собирая плевелы, вы вместе с ними не вырвали с корнем пшеницу.',2,1,13,29);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Оставьте и то и другое <1>расти вместе до <а>жатвы, а во время жатвы я скажу жнецам: «<б>Соберите прежде плевелы и свяжите их в связки, чтобы <в>сжечь их, а <г>пшеницу соберите в мой амбар».',2,1,13,30);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('<а>Другую притчу предложил Он им и сказал: Царство небес подобно <1б>горчичному зерну, которое человек взял и посеял в своём поле',2,1,13,31);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И которое меньше всех семян; но когда оно вырастет, тогда оно больше огородных растений и становится <1>деревом, так что прилетают небесные <2а>птицы и гнездятся в его ветвях.',2,1,13,32);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('<а>Другую притчу сказал Он им: Царство небес подобно <1б>закваске, которую <2в>женщина взяла и скрыла в <г>трёх мерах <3>муки, пока всё не заквасилось.',2,1,13,33);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Всё это Иисус говорил толпам в <а>притчах и без притчи ничего не говорил им,',2,1,13,34);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Чтобы исполнилось сказанное через пророка, который говорит: «<а>Я открою в притчах Мои уста; изреку <б>скрытое <1в>от основания мира».',2,1,13,35);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Тогда, оставив толпы, Он вошёл в <а>дом. И подошли к Нему Его ученики и сказали: Разъясни нам <б>притчу о плевелах в поле.',2,1,13,36);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И Он сказал в ответ: Тот, кто сеет хорошее семя, — <а>Сын Человеческий;',2,1,13,37);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Поле — мир; <1>хорошее семя — это <а>сыновья царства; плевелы — <б>сыновья лукавого;',2,1,13,38);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Враг, который посеял их, — <а>дьявол; жатва — <б>завершение века; а жнецы — ангелы.',2,1,13,39);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Итак, как плевелы собирают и сжигают огнём, так будет и при завершении века.',2,1,13,40);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('<а>Сын Человеческий пошлёт Своих ангелов, и они соберут из <1>Его <б>царства все <в>преткновения и тех, кто делает <г>беззаконие,',2,1,13,41);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И бросят их в <1а>огненную печь. Там будет <2б>плач и скрежет зубов.',2,1,13,42);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Тогда <1>праведники <а>воссияют, как солнце, в <2б>царстве их Отца. Кто имеет <в>уши, чтобы слышать, пусть слышит.',2,1,13,43);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Царство небес подобно скрытому в <1а>поле <2>сокровищу, которое <3>человек нашёл и <б>скрыл и от радости идёт и продаёт всё, что имеет, и <в>покупает то поле.',2,1,13,44);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Ещё царство небес подобно <1>купцу, который ищет хорошие жемчужины.',2,1,13,45);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И, найдя одну очень ценную <1а>жемчужину, он пошёл и продал всё, что имел, и <б>купил её.',2,1,13,46);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Ещё царство небес подобно <1а>неводу, закинутому в <2>море и собравшему из <3>всякого рода,',2,1,13,47);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Который, когда он наполнился, вытащили на берег и, сев, <1>хорошее собрали в сосуды, а негодное выбросили.',2,1,13,48);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Так будет при <а>завершении века: выйдут ангелы, и отделят злых из среды праведных,',2,1,13,49);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И бросят их в <1а>огненную печь. Там будет <2б>плач и скрежет зубов.',2,1,13,50);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Поняли ли вы всё это? — Они говорят Ему: Да.',2,1,13,51);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И Он сказал им: Поэтому всякий <а>книжник, <б>сделавшийся учеником царства небес, подобен хозяину дома, который выносит из своей <1>сокровищницы <в>новое и старое.',2,1,13,52);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И когда Иисус окончил эти притчи, Он ушёл оттуда.',2,1,13,53);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('<а>И, придя к Себе на родину, учил их в их <б>синагоге, так что они <в>изумлялись и говорили: Откуда у этого человека такая <г>мудрость и явления силы?',2,1,13,54);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('Разве это не <а>сын <1>плотника? Разве не Его <б>мать зовут Мария, а Его <в>братьев — Иаков, Иосиф, Симон и Иуда?',2,1,13,55);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И Его сёстры — разве не все они у нас? Откуда же у этого человека всё это?',2,1,13,56);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И они <а>претыкались из-за Него. А Иисус сказал им: <б>Пророк нигде не бывает без почёта, кроме как на родине и в своём доме.',2,1,13,57);
insert into bible_verse (text, testament_id, book_id, chapter, verse) values ('И Он <1>не совершил там многих явлений силы из-за их неверия.',2,1,13,58);